import React, { useState } from "react";
import { useParams, Link } from "react-router-dom";

const playerNames = ["Player 1", "Player 2", "Player 3", "Player 4"];
const categories = ["Cards Eaten", "Diamond Cards Eaten", "Queens", "Kings"];

function GameTracker() {
  const { roundId, gameId } = useParams();

  const [scores, setScores] = useState(
    playerNames.map(() =>
      categories.reduce((acc, category) => ({ ...acc, [category]: 0 }), {})
    )
  );

  const updateScore = (playerIndex, category, delta) => {
    const newScores = [...scores];
    newScores[playerIndex][category] += delta;
    setScores(newScores);
  };

  return (
    <div className="counter-page">
      <h2>Round {roundId} - Game {gameId}</h2>
      {scores.map((playerScore, i) => (
        <div key={i} className="player-card">
          <h3>{playerNames[i]}</h3>
          {categories.map((cat) => (
            <div key={cat} className="controls">
              <span>{cat}</span>
              <button onClick={() => updateScore(i, cat, -1)}>-</button>
              <span>{playerScore[cat]}</span>
              <button onClick={() => updateScore(i, cat, 1)}>+</button>
            </div>
          ))}
        </div>
      ))}
      <Link to={`/round/${roundId}`}>
        <button className="back-btn">‚Üê Back to Round {roundId}</button>
      </Link>
    </div>
  );
}

export default GameTracker;
