import React, { useState } from 'react';
import DoublePopup from './DoublePopup';
import './PlayerInput.css';

const cardTypes = ['l6oosh', 'diamonds', 'queens', 'kings'];

function PlayerInput({ mode, scores, setScores, rounds, setRounds, cardCounts, setCardCounts }) {
  const players = mode === 'free' ? ['P1', 'P2', 'P3', 'P4'] : ['Team 1', 'Team 2'];
  const [selectedCard, setSelectedCard] = useState(null);
  const [selectedPlayer, setSelectedPlayer] = useState(null);

  const handleCard = (type, player) => {
    if (type === 'queens' || type === 'kings') {
      setSelectedCard(type);
      setSelectedPlayer(player);
    } else {
      updateScore(type, player);
    }
  };

  const updateScore = (type, player, doubled = false, dealer = null) => {
    const newScores = { ...scores };
    const points = getPoints(type, doubled, player, dealer);
    newScores[player] = (newScores[player] || 0) + points[player];
    if (dealer && dealer !== player) {
      newScores[dealer] = (newScores[dealer] || 0) + points[dealer];
    }

    const newCounts = { ...cardCounts };
    if (type === 'l6oosh' || type === 'diamonds') {
      newCounts[type]++;
    } else {
      newCounts[type] = [...newCounts[type], { player, doubled, dealer }];
    }

    setScores(newScores);
    setCardCounts(newCounts);
    setSelectedCard(null);
    setSelectedPlayer(null);
  };

  const getPoints = (type, doubled, eater, dealer) => {
    const base = {
      l6oosh: -15,
      diamonds: -10,
      queens: doubled ? -50 : -25,
      kings: doubled ? -150 : -75,
    };
    const dealerBonus = {
      queens: 25,
      kings: 75,
    };
    const points = { [eater]: base[type] };
    if (doubled && dealer && dealer !== eater) {
      points[dealer] = dealerBonus[type];
    }
    return points;
  };

  return (
    <div className="player-input">
      {players.map(player => (
        <div key={player} className="player">
          <h3>{player}</h3>
          {cardTypes.map(type => (
            <button key={type} onClick={() => handleCard(type, player)}>{type}</button>
          ))}
          <p>Total: {scores[player] || 0}</p>
        </div>
      ))}
      {selectedCard && (
        <DoublePopup
          type={selectedCard}
          player={selectedPlayer}
          players={players}
          onSubmit={(doubled, dealer) => updateScore(selectedCard, selectedPlayer, doubled, dealer)}
          onClose={() => {
            setSelectedCard(null);
            setSelectedPlayer(null);
          }}
        />
      )}
    </div>
  );
}

export default PlayerInput;
