import React, { useState, useEffect } from 'react';
import PlayerInput from './PlayerInput';
import ScoreSheet from './ScoreSheet';
import CardCounter from './CardCounter';
import './GamePage.css';

const initialData = {
  l6oosh: 0, diamonds: 0, queens: [], kings: [],
};

function GamePage({ mode, resetFlag }) {
  const [scores, setScores] = useState({});
  const [rounds, setRounds] = useState({});
  const [cardCounts, setCardCounts] = useState(initialData);
  const [showSheet, setShowSheet] = useState(false);

  useEffect(() => {
    if (resetFlag) {
      setScores({});
      setRounds({});
      setCardCounts(initialData);
    }
  }, [resetFlag]);

  return (
    <div className="game-page">
      <CardCounter cardCounts={cardCounts} />
      <PlayerInput
        mode={mode}
        scores={scores}
        setScores={setScores}
        rounds={rounds}
        setRounds={setRounds}
        cardCounts={cardCounts}
        setCardCounts={setCardCounts}
      />
      <button className="score-sheet-button" onClick={() => setShowSheet(true)}>Score Sheet</button>
      {showSheet && (
        <ScoreSheet rounds={rounds} close={() => setShowSheet(false)} />
      )}
    </div>
  );
}

export default GamePage;
