// src/GameTracker.js
import React, { useState } from "react";
import { useParams, Link } from "react-router-dom";
import { FaGem, FaCrown, FaChessQueen } from "react-icons/fa";
import { CgCardSpades } from "react-icons/cg";
import "./GameTracker.css"; // Make sure to create and use this for styles

const initialNames = ["Player 1", "Player 2", "Player 3", "Player 4"];

const maxValues = {
  L6oosh: 13,
  Diamonds: 13,
  Queens: 4,
  King: 1,
};

const categories = [
  { label: "L6oosh", icon: <CgCardSpades color="#f87171" /> },
  { label: "Diamonds", icon: <FaGem color="#60a5fa" /> },
  { label: "Queens", icon: <FaChessQueen color="#facc15" /> },
  { label: "King", icon: <FaCrown color="#a78bfa" /> },
];

function GameTracker() {
  const { roundId, gameId } = useParams();
  const [names, setNames] = useState(initialNames);
  const [modalInfo, setModalInfo] = useState(null);
  const [scores, setScores] = useState(
    initialNames.map(() =>
      categories.reduce(
        (acc, cat) => ({
          ...acc,
          [cat.label]: {
            count: 0,
            doubled: false,
            playedBy: null,
          },
        }),
        {}
      )
    )
  );

  const handleNameChange = (index, value) => {
    const newNames = [...names];
    newNames[index] = value;
    setNames(newNames);
  };

  const openModal = (playerIdx, catLabel) => {
    setModalInfo({
      playerIdx,
      catLabel,
      doubled: false,
      playedBy: null,
    });
  };

  const closeModal = () => {
    setModalInfo(null);
  };

  const submitModal = () => {
    const { playerIdx, catLabel, doubled, playedBy } = modalInfo;
    const newScores = [...scores];
    const max = maxValues[catLabel];
    const cat = newScores[playerIdx][catLabel];

    if (cat.count < max) {
      cat.count += 1;
      cat.doubled = doubled;
      cat.playedBy = doubled ? playedBy : null;
    }

    setScores(newScores);
    closeModal();
  };

  const updateCount = (playerIdx, catLabel, delta) => {
    const cat = scores[playerIdx][catLabel];
    const max = maxValues[catLabel];

    if (delta === 1 && (catLabel === "Queens" || catLabel === "King")) {
      openModal(playerIdx, catLabel);
      return;
    }

    const newScores = [...scores];
    cat.count = Math.max(0, Math.min(cat.count + delta, max));
    if (cat.count === 0) {
      cat.doubled = false;
      cat.playedBy = null;
    }
    setScores(newScores);
  };

  const calculateScore = (playerIdx) => {
    let score = 0;
    const player = scores[playerIdx];

    for (const cat of categories) {
      const { count, doubled } = player[cat.label];
      if (cat.label === "L6oosh") score += count * -15;
      if (cat.label === "Diamonds") score += count * -10;
      if (cat.label === "Queens") score += count * (doubled ? -50 : -25);
      if (cat.label === "King") score += count * (doubled ? -150 : -75);
    }

    // Bonuses
    scores.forEach((other, idx) => {
      if (idx === playerIdx) return;
      ["Queens", "King"].forEach((catLabel) => {
        const bonus = catLabel === "Queens" ? 25 : 75;
        const { count, doubled, playedBy } = other[catLabel];
        if (doubled && playedBy === playerIdx) {
          score += count * bonus;
        }
      });
    });

    return score;
  };

  return (
    <div className="counter-page">
      <h2>Round {roundId} - Game {gameId}</h2>
      <div className="players-grid">
        {scores.map((playerScore, i) => (
          <div key={i} className="player-card">
            <input
              className="name-input"
              value={names[i]}
              onChange={(e) => handleNameChange(i, e.target.value)}
            />
            {categories.map((cat) => (
              <div key={cat.label} className="controls">
                <span>{cat.icon}</span>
                <button onClick={() => updateCount(i, cat.label, -1)}>-</button>
                <span>{playerScore[cat.label].count}</span>
                <button onClick={() => updateCount(i, cat.label, 1)}>+</button>
              </div>
            ))}
            <div className="score-display">Total: {calculateScore(i)}</div>
          </div>
        ))}
      </div>

      {modalInfo && (
        <div className="modal-backdrop">
          <div className="modal-content">
            <h3>{modalInfo.catLabel} Options</h3>
            <label>
              <input
                type="checkbox"
                checked={modalInfo.doubled}
                onChange={(e) =>
                  setModalInfo((prev) => ({
                    ...prev,
                    doubled: e.target.checked,
                    playedBy: e.target.checked ? 0 : null,
                  }))
                }
              />
              Doubled?
            </label>

            {modalInfo.doubled && (
              <select
                value={modalInfo.playedBy ?? ""}
                onChange={(e) =>
                  setModalInfo((prev) => ({
                    ...prev,
                    playedBy: parseInt(e.target.value),
                  }))
                }
              >
                <option value="">Who played it?</option>
                {names.map((n, idx) => (
                  <option key={idx} value={idx}>
                    {n}
                  </option>
                ))}
              </select>
            )}
            <div className="modal-actions">
              <button onClick={submitModal}>Confirm</button>
              <button onClick={closeModal} className="cancel">Cancel</button>
            </div>
          </div>
        </div>
      )}

      <Link to={`/round/${roundId}`}>
        <button className="back-btn">‚Üê Back to Round {roundId}</button>
      </Link>
    </div>
  );
}

export default GameTracker;
