import React from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import RoundPage from "./RoundPage";
import GameTracker from "./GameTracker";
import { useScoreStore } from "./useScoreStore";

function HomePage() {
  const { resetAll } = useScoreStore();

  return (
    <div className="round-select">
      <h1>üÉè Card Game Tracker</h1>
      <div className="button-grid">
        {[1, 2, 3, 4].map((round) => (
          <Link key={round} to={`/round/${round}`}>
            <button>Round {round}</button>
          </Link>
        ))}
      </div>
      <button onClick={resetAll} className="back-btn" style={{ marginTop: "40px" }}>
        üîÑ Reset All Scores
      </button>
    </div>
  );
}

function App() {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<HomePage />} />
        <Route path="/round/:roundId" element={<RoundPage />} />
        <Route path="/round/:roundId/game/:gameId" element={<GameTracker />} />
      </Routes>
    </Router>
  );
}

export default App;
