import React, { useState } from "react";
import { useParams, Link } from "react-router-dom";
import { FaGem, FaCrown, FaChessQueen } from "react-icons/fa";
import { CgCardSpades } from "react-icons/cg";

const initialNames = ["Player 1", "Player 2", "Player 3", "Player 4"];
const maxValues = {
  L6oosh: 13,
  Diamonds: 13,
  Queens: 4,
  King: 1,
};

const categories = [
  { label: "L6oosh", icon: <CgCardSpades color="#f87171" /> },
  { label: "Diamonds", icon: <FaGem color="#60a5fa" /> },
  { label: "Queens", icon: <FaChessQueen color="#facc15" /> },
  { label: "King", icon: <FaCrown color="#a78bfa" /> },
];

function GameTracker() {
  const { roundId, gameId } = useParams();

  const [names, setNames] = useState(initialNames);
  const [scores, setScores] = useState(
    initialNames.map(() =>
      categories.reduce(
        (acc, cat) => ({
          ...acc,
          [cat.label]: {
            count: 0,
            doubled: false,
            playedBy: null,
          },
        }),
        {}
      )
    )
  );

  const updateCount = (playerIdx, catLabel, delta) => {
    const newScores = [...scores];
    const cat = newScores[playerIdx][catLabel];
    const max = maxValues[catLabel];

    cat.count = Math.max(0, Math.min(cat.count + delta, max));
    setScores(newScores);
  };

  const toggleDoubled = (playerIdx, catLabel) => {
    const newScores = [...scores];
    newScores[playerIdx][catLabel].doubled =
      !newScores[playerIdx][catLabel].doubled;

    if (!newScores[playerIdx][catLabel].doubled) {
      newScores[playerIdx][catLabel].playedBy = null;
    }

    setScores(newScores);
  };

  const setPlayedBy = (playerIdx, catLabel, playedByIdx) => {
    const newScores = [...scores];
    newScores[playerIdx][catLabel].playedBy = playedByIdx;
    setScores(newScores);
  };

  const handleNameChange = (index, value) => {
    const newNames = [...names];
    newNames[index] = value;
    setNames(newNames);
  };

  const calculateScore = (playerIdx) => {
    let score = 0;
    const player = scores[playerIdx];

    for (const cat of categories) {
      const { count, doubled, playedBy } = player[cat.label];

      switch (cat.label) {
        case "L6oosh":
          score += count * -15;
          break;
        case "Diamonds":
          score += count * -10;
          break;
        case "Queens":
          score += doubled
            ? count * -50
            : count * -25;

          // Add bonus to "playedBy" if set
          if (doubled && playedBy !== null) {
            score += (playedBy === playerIdx ? 0 : 0); // Prevent self-play bonus
          }
          break;
        case "King":
          score += doubled
            ? count * -150
            : count * -75;

          if (doubled && playedBy !== null) {
            score += (playedBy === playerIdx ? 0 : 0);
          }
          break;
        default:
          break;
      }
    }

    // Bonus from other players
    for (let i = 0; i < scores.length; i++) {
      if (i === playerIdx) continue;

      const other = scores[i];
      for (const cat of ["Queens", "King"]) {
        const { count, doubled, playedBy } = other[cat];
        const bonus =
          cat === "Queens" ? 25 : 75;

        if (doubled && playedBy === playerIdx) {
          score += count * bonus;
        }
      }
    }

    return score;
  };

  return (
    <div className="counter-page">
      <h2>Round {roundId} - Game {gameId}</h2>
      <div className="players-grid">
        {scores.map((playerScore, i) => (
          <div key={i} className="player-card">
            <input
              className="name-input"
              value={names[i]}
              onChange={(e) => handleNameChange(i, e.target.value)}
            />
            {categories.map((cat) => {
              const item = playerScore[cat.label];
              return (
                <div key={cat.label} className="controls">
                  <span>{cat.icon}</span>
                  <button onClick={() => updateCount(i, cat.label, -1)}>-</button>
                  <span>{item.count}</span>
                  <button onClick={() => updateCount(i, cat.label, 1)}>+</button>
                </div>
              );
            })}
            {["Queens", "King"].map((catName) => {
              const cat = playerScore[catName];
              return (
                <div key={catName} className="extras">
                  <label>
                    <input
                      type="checkbox"
                      checked={cat.doubled}
                      onChange={() => toggleDoubled(i, catName)}
                    />
                    Double {catName}
                  </label>
                  {cat.doubled && cat.count > 0 && (
                    <select
                      value={cat.playedBy ?? ""}
                      onChange={(e) =>
                        setPlayedBy(i, catName, parseInt(e.target.value))
                      }
                    >
                      <option value="">Played by...</option>
                      {names.map((name, idx) => (
                        <option key={idx} value={idx}>
                          {name}
                        </option>
                      ))}
                    </select>
                  )}
                </div>
              );
            })}
            <div className="score-display">Total: {calculateScore(i)}</div>
          </div>
        ))}
      </div>
      <Link to={`/round/${roundId}`}>
        <button className="back-btn">‚Üê Back to Round {roundId}</button>
      </Link>
    </div>
  );
}

export default GameTracker;
