import React from 'react';
import './ScoreSheet.css';

function ScoreSheet({ onClose, rounds, playerNames = [] }) {
  const safeRounds = Array.isArray(rounds) ? rounds : [];

  return (
    <div className="score-sheet-overlay">
      <div className="score-sheet-modal">
        <button className="close-button" onClick={onClose}>Close</button>
        <h2>Score Sheet</h2>
        <table>
          <thead>
            <tr>
              <th>Player</th>
              {safeRounds.map((_, idx) => (
                <th key={idx}>
                  Complex {Math.floor(idx / 2) + 1} / {(idx % 2) + 1}
                </th>
              ))}
            </tr>
          </thead>
          <tbody>
            {playerNames.map((player, i) => (
              <tr key={i}>
                <td>{player}</td>
                {safeRounds.map((round, j) => (
                  <td key={j}>{round[player] || 0}</td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default ScoreSheet;
