import React, { useState } from 'react';
import './HomePage.css';

function HomePage({ setMode, setPlayerNames, handleReset }) {
  const [names, setNames] = useState(['', '', '', '']);

  const startGame = (mode) => {
    const filtered = names.map((n, i) => n || `P${i + 1}`);
    setPlayerNames(mode === 'free' ? filtered : [filtered[0] || 'Team 1', filtered[1] || 'Team 2']);
    setMode(mode);
  };

  return (
    <div className="home-page">
      <h1>Complex CC</h1>
      <input placeholder="Player 1" onChange={(e) => names[0] = e.target.value} />
      <input placeholder="Player 2" onChange={(e) => names[1] = e.target.value} />
      <input placeholder="Player 3" onChange={(e) => names[2] = e.target.value} />
      <input placeholder="Player 4" onChange={(e) => names[3] = e.target.value} />
      <button onClick={() => startGame('free')}>Free-for-all</button>
      <button onClick={() => startGame('partners')}>Partners</button>
      <button className="reset" onClick={handleReset}>Reset Scores</button>
    </div>
  );
}

export default HomePage;
