import React, { useState } from 'react';
import HomePage from './components/HomePage';
import GamePage from './components/GamePage';
import './App.css';

function App() {
  const [mode, setMode] = useState(null);
  const [playerNames, setPlayerNames] = useState([]);
  const [resetFlag, setResetFlag] = useState(false);

  const handleReset = () => {
    setResetFlag(true);
    setTimeout(() => setResetFlag(false), 0);
    setMode(null);
    setPlayerNames([]);
  };

  return (
    <div className="app">
      {!mode ? (
        <HomePage setMode={setMode} setPlayerNames={setPlayerNames} handleReset={handleReset} />
      ) : (
        <GamePage mode={mode} resetFlag={resetFlag} playerNames={playerNames} goHome={() => setMode(null)} />
      )}
    </div>
  );
}

export default App;
