import React, { useState } from 'react';
import PlayerInput from './PlayerInput';
import ScoreSheet from './ScoreSheet';
import CardCounter from './CardCounter';
import './GamePage.css';

const roundNames = [
  'Complex 1.1', 'Complex 1.2', 'Complex 2.1', 'Complex 2.2',
  'Complex 3.1', 'Complex 3.2', 'Complex 4.1', 'Complex 4.2',
];

function GamePage({ mode, playerNames, scores, setScores, rounds, setRounds, cardCounts, setCardCounts, goHome }) {
  const [roundIndex, setRoundIndex] = useState(0);
  const [showSheet, setShowSheet] = useState(false);

  const nextRound = () => {
    const updated = { ...rounds };
    const name = roundNames[roundIndex];
    updated[name] = { ...scores };
    setRounds(updated);
    if (roundIndex < roundNames.length - 1) {
      setRoundIndex(roundIndex + 1);
    }
  };

  const previousRound = () => {
    if (roundIndex > 0) {
      const name = roundNames[roundIndex - 1];
      const prevScores = rounds[name] || {};
      setScores(prevScores);
      setRoundIndex(roundIndex - 1);
    }
  };

  return (
    <div className="game-page">
      <CardCounter cardCounts={cardCounts} />
      <button className="back" onClick={goHome}>‚Üê Home</button>
      <h2>{roundNames[roundIndex]}</h2>
      <PlayerInput
        mode={mode}
        scores={scores}
        setScores={setScores}
        rounds={rounds}
        setRounds={setRounds}
        cardCounts={cardCounts}
        setCardCounts={setCardCounts}
        playerNames={playerNames}
      />
      <div className="bottom-buttons">
        <button onClick={previousRound}>Previous Round</button>
        <button className="score-sheet-button" onClick={() => setShowSheet(true)}>Score Sheet</button>
        <button onClick={nextRound}>Next Round</button>
      </div>
      {showSheet && (
        <ScoreSheet rounds={rounds} onClose={() => setShowSheet(false)} players={playerNames} />
      )}
    </div>
  );
}

export default GamePage;
