import React, { useState } from "react";
import { useParams, Link } from "react-router-dom";
import "./GameCounter.css";

function GameCounter() {
  const { id } = useParams();
  const [cardsEaten, setCardsEaten] = useState(0);
  const [diamondCards, setDiamondCards] = useState(0);
  const [queens, setQueens] = useState(0);
  const [kings, setKings] = useState(0);

  const counters = [
    { label: "Cards Eaten", value: cardsEaten, setter: setCardsEaten },
    { label: "Diamond Cards Eaten", value: diamondCards, setter: setDiamondCards },
    { label: "Queens", value: queens, setter: setQueens },
    { label: "Kings", value: kings, setter: setKings },
  ];

  return (
    <div className="counter-page">
      <h2>Player {id} Score Tracker</h2>
      <div className="counters">
        {counters.map((item, i) => (
          <div key={i} className="counter">
            <h3>{item.label}</h3>
            <div className="controls">
              <button onClick={() => item.setter(item.value - 1)}>-</button>
              <span>{item.value}</span>
              <button onClick={() => item.setter(item.value + 1)}>+</button>
            </div>
          </div>
        ))}
      </div>
      <Link to="/">
        <button className="back-btn">‚Üê Back to Home</button>
      </Link>
    </div>
  );
}

export default GameCounter;
